import {NOTE_TYPE} from "../data/data"

const TextInput = props => {

    const isValid = (e) => {

        if(props.field !== "Create new Project"){

            if (e.target.value.length > 0) {
                props.setValue(e.target.value)
            }

            if (props.type === NOTE_TYPE.TEXT) {
                if ((props.field === "Title" || props.field === "Text") && e.target.value.length > 0) {
                    props.enable(true)
                } else {
                    props.enable(false)

                }
            } else {
                if(props.field === "url" && e.target.value.length > 0) {
                    props.enable(true)
                } else {
                    props.enable(false)
                }
            }
        } else {
            props.setValue(e.target.value)
        }
    }

    let style = "form-control "
    const setErrorStyle = () => {
        style = "form-control red-border"
    }


    return (

        <div className="form-group align-center">
            <label htmlFor="formGroupExampleInput">{props.field}</label>
            <input type="text" class={style} placeholder={props.field} onChange={isValid}/>
        </div>
    )
}

export default TextInput;
