import data, { NO_PROJECT } from "../data/data"
import Project from "../components/Project"
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/styles.css';
import {useState} from "react";

import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Nav from 'react-bootstrap/Nav';
import Tab from 'react-bootstrap/Tab'
import Tabs from 'react-bootstrap/Tabs'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import ListGroup from 'react-bootstrap/ListGroup'
import { NavLink } from "react-bootstrap";
import Navlink from "../components/Navlink"
const projectType = (project) => {
    if (project == NO_PROJECT) {
        return true
    }
    return false
}
const projects = new Array();
const noprojects = new Array();

(function(){


    Object.keys(data).map ((project, idx) => {
        if (project !== NO_PROJECT) {
            projects.push(project)
                        //<Project key={idx} project={project} notes={data[project]}/>)
                        //console.log("notes: ", data[project]);
        } else {
            noprojects.push(<Project key={idx} project={project} notes={data[project]}/>);
        }
    })
})();

const allProjects = new Array();
(function() {


    Object.keys(data).map ((project, idx) => {
        allProjects.push(project)
    })
})();

const variants = ["info","primary","secondary","success","danger","warning","info","light"]

const random = () => {
    return variants[Math.floor(Math.random() * variants.length)];
}

const addNote = () => {
    <div class="modal" tabindex="-1" role="dialog">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <p>Modal body text goes here.</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary">Save changes</button>
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>
}



const App = () =>{

    const [toRender, setToRender] = useState(allProjects);
    const [dogs, setDogs] = useState(true);


    return ( <div>
                <nav className="navbar navbar-light bg-light">
                    <div className="container-fluid">
                        <span className="navbar-brand mb-0 h1 mx-auto">All we do is take notes.</span>
                    </div>
                </nav>

                <div className="text-center bg-light">
                    <div class="btn-group" role="group" aria-label="Basic example">
                        <button type="button" class="btn btn-light" onClick={() => setToRender(allProjects)}>
                            All Notes
                        </button>
                        { Object.keys(data).map((project,idx) => 
                            <>
                                <button type="button" class="btn btn-light" onClick={() => setToRender([project])}>
                                    {project}
                                </button>
                            </>

                        )}
                        <button type="button" class="btn btn-info" onClick={() => setToRender()}>
                            + Add
                        </button>
                    </div>
                </div>

                <div className="container">
                    { Object.keys(data).map((project,idx) => 
                        ((toRender.includes(project)) ?
                            <Project key={idx} project={project} notes={data[project]}/> : "")
                        )}
                </div>
            </div>
    )
}


export default App;
