import data, { NOTE_TYPE, NO_PROJECT } from "../data/data"
import Project from "../components/Project"
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/styles.css';
import {useState} from "react";
import AddNote from "../components/AddNote"
import Note from "../components/Note"

const allProjects = new Array();
(function() {
    Object.keys(data).map ((project, idx) => {
        allProjects.push(project)
    })
})();

class textNote  {
    project = "";
    type = NOTE_TYPE.TEXT;
    title = "";
    text = "";
}

class linkNote  {
    project = "";
    type = NOTE_TYPE.LINK;
    url = "";
    text = "";
}


const App = () => {

    const [toRender, setToRender] = useState(allProjects);
    const [newData, setData] = useState([]);
    console.log("newData: ", newData)
    const addNote = (selectedProject,selectedType,title,text,url) => {

        if(!allProjects.includes(selectedProject)){
            allProjects.push(selectedProject)
        }
        console.log("to render: ", allProjects)

        let note;
        if (selectedType == NOTE_TYPE.TEXT) {
            note = new textNote(selectedProject, NOTE_TYPE.TEXT, title, text)
        }
        if(selectedType == NOTE_TYPE.LINK) {
            note = new linkNote(selectedProject, NOTE_TYPE.LINK, url,text)
        }

        newData.push(note)
        console.log("note: ", note)
        console.log("new data: ", newData)
        setData(newData)

    }

    return ( <div>
                <nav className="navbar navbar-light bg-light">
                    <div className="container-fluid">
                        <span className="navbar-brand mb-0 h1 mx-auto">All we do is take notes.</span>
                    </div>
                </nav>

                <div id="projects" className="text-center bg-light">
                    <div className="btn-group" role="group" aria-label="Basic example">
                        <button type="button" className="btn btn-light" onClick={() => setToRender(allProjects)}>
                            All Notes
                        </button>
                        { Object.keys(data).map((project,idx) =>
                                ((project != NO_PROJECT) ? 
                                <button type="button" className="btn btn-light" onClick={() => setToRender([project])}>
                                    {project}
                                </button>: "")
                        )}
                        <button type="button" className="btn btn-info" data-bs-toggle="collapse" data-bs-target="#collapseExample">
                            + Add
                        </button>
                    </div>

                </div>
                <div className="collapse mx-auto" id="collapseExample">
                    <AddNote callback={setData} projects={allProjects} data={newData}/>
                </div>
                <div className="container">
                    <div>
                    { Object.keys(data).map((project,idx) => 
                        ((toRender.includes(project)) ?
                            <Project key={idx} project={project} notes={data[project]}/> : "")
                        )
                    }
                    </div>
                    <div className="row row-cols-2 row-cols-sm-3 row-cols-md-4">>
                    {Object.keys(newData).map((each,idx) => (

                        <Note project={newData[each].project} type={newData[each].type} text={newData[each].text} title={newData[each].title} url={newData[each].url}/>

                    ))
                    }
                    </div>
                </div>
            </div>
    )
}


export default App;
