import {NOTE_TYPE} from "../data/data"

const TextInput = props => {

    const isValid = (e) => {

        if(props.field !== "Create new Project"){

        if (e.target.value.length > 0) {
            props.markPresent(true)
            props.setValue(e.target.value)
        } else {
            props.markPresent(false)
            props.setValue(e.target.value)
        }

        if (props.type == NOTE_TYPE.TEXT) {
            if ((props.field == "Title" || props.field == "Text") && e.target.value.length > 0) {
                props.enable(true)
            } else {
                props.enable(false)
            }
        } else {
            if(props.field == "url" && e.target.value.length > 0) {
                props.enable(true)
            } else {
                props.enable(false)
            }
        }
    }
    }

    return (

        <div class="form-group align-center">
            <label for="formGroupExampleInput">{props.field}</label>
            <input type="text" class="form-control" id="formGroupExampleInput" placeholder={props.field} onChange={props.callback}/>
        </div>
    )
}

export default TextInput;
