import SelectProject from "./SelectProject"
import SelectType from "./SelectType"
import TextInput from "./TextInput"
import {useState} from "react";
import AddCancel from "./AddCancel";
import { NO_PROJECT } from "../data/data";



const AddNote = props => {

        const [selectedProject, setProject] = useState(NO_PROJECT);
        const [selectedType, setType] = useState("Text");
        const [isTextPresent, setText] = useState(false);
        const [isTitlePresent, setTitle] = useState(false); 
        const [isUrlPresent, setUrl] = useState(false);
        const [selectedColor, setColor] = useState("light");
        const [isEnabled, setEnable] = useState("false");

        const isValid = () => {
            if (selectedType === "Text") {
                if (isTextPresent || isTitlePresent) {
                    setEnable("true")
                }
            } else {
                if (isUrlPresent) {
                    setEnable("true")
                }
            }
        }


        return (
            <>
                <div className="container">
                    <SelectProject projects={props.projects} selectedProject={setProject}/>
                    <SelectType selectedType={props.selectedType}/>
                    <TextInput type="Title" callback={setTitle}/>

                    <TextInput type="Text"/>
                    <TextInput type="url" />
                    <AddCancel enable={isEnabled}/>
                </div>


            </>
        )
}

export default AddNote;
