import SelectProject from "./SelectProject"
import SelectType from "./SelectType"
import TextInput from "./TextInput"
import {useState} from "react";
import AddCancel from "./AddCancel";
import { NO_PROJECT } from "../data/data";
import SelectColor from "./SelectColor";



const AddNote = props => {

        const [selectedProject, setProject] = useState(NO_PROJECT);
        const [newProject, setNewProject] = useState("");
        const [selectedType, setType] = useState("");
        const [isTextPresent, setTextPresent] = useState(false);
        const [text, setText] = useState("");
        const [isTitlePresent, setTitlePresent] = useState(false); 
        const [title, setTitle] = useState("");
        const [isUrlPresent, setUrlPresent] = useState(false);
        const [url, setUrl] = useState("");
        const [selectedColor, setColor] = useState("bg-warning");
        const [isEnabled, setEnable] = useState(false);

        const resetAll = () => {
            setProject(NO_PROJECT)
            setType("")
            setTextPresent(false)
            setText("")
            setTitlePresent(false)
            setTitle("")
            setUrlPresent(false)
            setUrl("")
            setColor("bg-warning")
            setEnable(false)
        }

        return (
            <>
                <div className="container">
                    <SelectProject projects={props.projects} callback={setProject}/>
                    <TextInput field="Create new Project" setValue={setNewProject}/>
                    <SelectType selectedType={props.selectedType} callback={setType} resetText={setText} resetTitle={setTitle} resetUrl={setUrl} enable={setEnable}/>
                    <TextInput field="Title" markPresent={setTitlePresent} setValue={setTitle} type={selectedType} enable={setEnable}/>

                    <TextInput field="Text" markPresent={setTextPresent} setValue={setText} type={selectedType} enable={setEnable}/>
                    <TextInput field="url" markPresent={setUrlPresent} setValue={setUrl} type={selectedType} enable={setEnable}/>
                    <SelectColor callback={setColor}/>
                    <AddCancel enable={isEnabled} data={props.data} callback={props.callback} project={(newProject.length > 0) ? newProject: selectedProject} type={selectedType} title={title} url={url} text={text} color={selectedColor}/>
                </div>


            </>
        )
}

export default AddNote;
