import Note from "./Note"


const variantsBg = ["bg-info","bg-dark","bg-secondary","bg-success","bg-danger","bg-warning"]

const randomBg = () => {
    return variantsBg[Math.floor(Math.random() * variantsBg.length)];
}

const Project = props => {
    const notes = props.notes;
    const bg = randomBg();
    return (<>

                <div className="row row-cols-2 row-cols-sm-3 row-cols-md-4">
                    {
                        Object.keys(notes).map((note,i) =>
                        <Note key={i} project={props.project} type={notes[note].type} text={notes[note].text} title={notes[note].title} url={notes[note].url} bg="bg-warning"/>
                    )}
                </div>
            </>)
}




export default Project;
