import data, { NO_PROJECT } from "../data/data"
import Project from "../components/Project"
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/styles.css';
import {useState} from "react";

import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Nav from 'react-bootstrap/Nav';
import Tab from 'react-bootstrap/Tab'
import Tabs from 'react-bootstrap/Tabs'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import ListGroup from 'react-bootstrap/ListGroup'
import { NavLink } from "react-bootstrap";
import Navlink from "../components/Navlink"
const projectType = (project) => {
    if (project == NO_PROJECT) {
        return true
    }
    return false
}
const projects = new Array();
const noprojects = new Array();

(function(){


    Object.keys(data).map ((project, idx) => {
        if (project !== NO_PROJECT) {
            projects.push(project)
                        //<Project key={idx} project={project} notes={data[project]}/>)
                        //console.log("notes: ", data[project]);
        } else {
            noprojects.push(<Project key={idx} project={project} notes={data[project]}/>);
        }
    })
})();

const variants = ["info","primary","secondary","success","danger","warning","info","light"]

const random = () => {
    return variants[Math.floor(Math.random() * variants.length)];
}



const App = () =>{

    const [toRender, setToRender] = useState(data);
    const [dogs, setDogs] = useState(true);

    return ( <div>
                <nav className="navbar navbar-light bg-light">
                    <div className="container-fluid">
                        <span className="navbar-brand mb-0 h1">All we do is take notes. Notes here, notes there, notes...EVERYWHERE.</span>
                    </div>
                </nav>

                <ul class="nav justify-content-center">
                    { Object.keys(data).map((project,idx) => 
                        <>
                            <li class="nav-item">
                                <a class="nav-link" aria-current="page" onChange={() => setToRender(project)}>
                                {project}
                                </a>
                            </li>
                        </>

                    )}
                </ul>

                <div className="container">

                    { Object.keys(toRender).map((project,idx) => 
                            <Project key={idx} project={project} notes={toRender[project]}/>
                    )}
                </div>
            </div>
    )
}


export default App;
