import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'


const variantsBg = ["info","primary","secondary","success","danger","warning","info","light"]

const randomBg = () => {
    return variantsBg[Math.floor(Math.random() * variantsBg.length)];
}

const Text = props => {
    return (

        <Card bg={randomBg()} key="1" text='white'>
            <Card.Header>{props.title}</Card.Header>
            <Card.Body>
                <Card.Text>{props.text}</Card.Text>
            </Card.Body>
        </Card>
        )
}

export default Text;
