import SelectProject from "./SelectProject"
import SelectType from "./SelectType"
import TextInput from "./TextInput"
import {useState} from "react";
import AddCancel from "./AddCancel";
import { NO_PROJECT } from "../data/data";
import SelectColor from "./SelectColor";
import NewOrExistingProj from "./NewOrExistingProj";


const AddNote = props => {

        const [selectedProject, setProject] = useState(NO_PROJECT);
        const [selectedTypeOfProject, setTypeOfProject] = useState("");
        const [newProject, setNewProject] = useState("");
        const [selectedType, setType] = useState("");
        const [text, setText] = useState("");
        const [title, setTitle] = useState("");
        const [url, setUrl] = useState("");
        const [selectedColor, setColor] = useState("bg-warning");
        const [isEnabled, setEnable] = useState(false);

        const clearFields = () => {
            setProject(NO_PROJECT)
            setNewProject("")
            setType("")
            setText("")
            setTitle("")
            setUrl("")
            console.log("cleared")
        }

    return (
        <>
            <div className="container">
                <NewOrExistingProj callback={setTypeOfProject}/>
                {(selectedTypeOfProject === "New") ? 
                <TextInput field="Enter a new Project name" setValue={setNewProject}/>:
                (selectedTypeOfProject === "Existing") ? 
                <SelectProject projects={props.projects} callback={setProject}/>:
                ""
                }


                <SelectType selectedType={props.selectedType} callback={setType} resetText={setText} resetTitle={setTitle} resetUrl={setUrl} enable={setEnable}/>

                {
                    (selectedType === "text") ? 
                    <>
                    <p>Enter at least one of Title or Content</p>
                    <TextInput field="Title" setValue={setTitle} type={selectedType} enable={setEnable}/>
                    <TextInput field="Content" setValue={setText} type={selectedType} enable={setEnable}/>
                    </>:
                    (selectedType === "link") ? 
                    <>
                    <TextInput field="url" setValue={setUrl} type={selectedType} enable={setEnable}/>

                    <TextInput field="display text for url (optional)" setValue={setTitle} type={selectedType} enable={setEnable}/>
                    </>:
                    ""

                }
                <SelectColor callback={setColor}/>
                <AddCancel enable={isEnabled} callback={props.callback} clearForm={clearFields} project={(newProject.length > 0) ? newProject: selectedProject} type={selectedType} title={title} url={url} text={text} color={selectedColor}/>
            </div>
        </>
    )
}

export default AddNote;
