import Note from "./Note"

import CardDeck from 'react-bootstrap/CardDeck'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

const variantsBg = ["bg-primary","primary","bg-secondary","bg-success","bg-danger","bg-warning","bg-warning"]

const randomBg = () => {
    return variantsBg[Math.floor(Math.random() * variantsBg.length)];
}

const Project = props => {
    const notes = props.notes;
    return (<>
                <div className="row">
                    <h4>{props.project}</h4>
                </div>
                <div className="row row-cols-2 row-cols-sm-3 row-cols-md-4">
                    {
                        Object.keys(notes).map((note,i) =>
                        <Note key={i} project={props.project} type={notes[note].type} text={notes[note].text} title={notes[note].title} url={notes[note].url}/>
                    )}
                </div>
            </>)
}




export default Project;
