import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'


const variantsBg = ["info","primary","secondary","success","danger","warning","info"]

const randomBg = () => {
    return variantsBg[Math.floor(Math.random() * variantsBg.length)];
}

const Text = props => {
    return (

        <div bg={randomBg()} key="1" text='light'>
            <div>{props.title}</div>
            <div>
                <div>{props.text}</div>
            </div>
        </div>
        )
}

export default Text;
