import data, { NOTE_TYPE, NO_PROJECT } from "../data/data"
import Project from "../components/Project"
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/styles.css';
import {useState} from "react";
import AddNote from "../components/AddNote"

import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Nav from 'react-bootstrap/Nav';
import Tab from 'react-bootstrap/Tab'
import Tabs from 'react-bootstrap/Tabs'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import ListGroup from 'react-bootstrap/ListGroup'
import { NavLink } from "react-bootstrap";
import Navlink from "../components/Navlink"
const projectType = (project) => {
    if (project == NO_PROJECT) {
        return true
    }
    return false
}
const projects = new Array();
const noprojects = new Array();

(function(){


    Object.keys(data).map ((project, idx) => {
        if (project !== NO_PROJECT) {
            projects.push(project)
                        //<Project key={idx} project={project} notes={data[project]}/>)
                        //console.log("notes: ", data[project]);
        } else {
            noprojects.push(<Project key={idx} project={project} notes={data[project]}/>);
        }
    })
})();

const allProjects = new Array();
(function() {


    Object.keys(data).map ((project, idx) => {
        allProjects.push(project)
    })
})();

const variants = ["info","primary","secondary","success","danger","warning","info","light"]

const random = () => {
    return variants[Math.floor(Math.random() * variants.length)];
}

let textNote = {
    type: NOTE_TYPE.TEXT,
    title: "",
    text: "",
}

let linkNote = {
    type: NOTE_TYPE.LINK,
    url: "",
    text: "",
}


const App = () => {

    const [toRender, setToRender] = useState(allProjects);

    const addNote = (selectedProject,selectedType,isTitlePresent,title,isTextPresent,text,isUrlPresent,url) => {
        let note = {}
        Object.keys(allProjects).map ((project, idx) => {

            if(project === selectedProject) {
                if (selectedType === NOTE_TYPE.TEXT) {
                    note = textNote(title,text)
                } else {
                    note = linkNote(url,text)
                }
            }
            allProjects[project].push(note)
        })
    }

    return ( <div>
                <nav className="navbar navbar-light bg-light">
                    <div className="container-fluid">
                        <span className="navbar-brand mb-0 h1 mx-auto">All we do is take notes.</span>
                    </div>
                </nav>

                <div className="text-center bg-light">
                    <div className="btn-group" role="group" aria-label="Basic example">
                        <button type="button" className="btn btn-light" onClick={() => setToRender(allProjects)}>
                            All Notes
                        </button>
                        { Object.keys(data).map((project,idx) =>
                                ((project != NO_PROJECT) ? 
                                <button type="button" className="btn btn-light" onClick={() => setToRender([project])}>
                                    {project}
                                </button>: "")
                        )}
                        <button type="button" className="btn btn-info" data-bs-toggle="collapse" data-bs-target="#collapseExample">
                            + Add
                        </button>
                    </div>

                </div>
                <div className="collapse mx-auto" id="collapseExample">
                    <AddNote callback={addNote} projects={allProjects}/>
                </div>
                <div className="container">
                    { Object.keys(data).map((project,idx) => 
                        ((toRender.includes(project)) ?
                            <Project key={idx} project={project} notes={data[project]}/> : "")
                        )}
                </div>
            </div>
    )
}


export default App;
